function renderBadges(props) {
  if(this.state.gameStart===false){
    return
  } else{
    return <div>
      {()=>{
        props.map(x => <div className={"badgeNumber"+[x]}>{}
        </div>)
      }}
    </div>
  }

}


class App extends Component {

  state = {
    score: 0,
    currentGame: "none",
    badges: [false,false,false,false],
    gameStart: true,
    gameOver: false,
  }


  componentDidMount = () => {

    // alert("hi")
    // renderBadges()

  }

  render() {
    return (
      <Router>
      <div>
      <div className="nav">

      </div>

      <div className="scoreboard">

        {renderBadges(this.state.badges)}

      </div>

        <Switch>
          <Route exact path="/">
            <Main />
          </Route>
          <Route path="/map">
          </Route>
          <Route path="/*">
          </Route>
        </Switch>
      </div>
    </Router>
    )
  }
}

export default App;
