import React, { Component } from 'react';
import styled from 'styled-components';
import { render } from "react-dom";

import CanvasDraw from "react-canvas-draw";
import classNames from "./drawing.css";

const DrawingWrapper = styled.div`

    display: flex;
    flex-direction: row-reverse;

    #toolbox {
        padding: 20p;x
    }

`

class CanvasDraw2 extends Component {

    constructor(props){
        super(props);
      };

    state = {
    color: "#ffc600",
    // width: 100,
    // height: 100,
    brushRadius: 10,
    lazyRadius: 12
  };
  componentDidMount() {
    // let's change the color randomly every 2 seconds. fun!
    window.setInterval(() => {
      this.setState({
        color: "#" + Math.floor(Math.random() * 16777215).toString(16)
      });
    }, 2000);
    console.log("image source " + this.props.imgSrc)
  }
  render() {
    return (
      <DrawingWrapper>

          <div id="toolbox" className={classNames.tools}>
            <button
              onClick={() => {
                localStorage.setItem(
                  "savedDrawing",
                  this.saveableCanvas.getSaveData()
                );
              }}
            >
              Save
            </button>
            <button
              onClick={() => {
                this.saveableCanvas.clear();
              }}
            >
              Clear
            </button>
            <button
              onClick={() => {
                this.saveableCanvas.undo();
              }}
            >
              Undo
            </button>
            <div>
              <label>Width:</label>
              <input
                type="number"
                value={this.state.width}
                onChange={e =>
                  this.setState({ width: parseInt(e.target.value, 10) })
                }
              />
            </div>
            <div>
              <label>Height:</label>
              <input
                type="number"
                value={this.state.height}
                onChange={e =>
                  this.setState({ height: parseInt(e.target.value, 10) })
                }
              />
            </div>
            <div>
              <label>Brush-Radius:</label>
              <input
                type="number"
                value={this.state.brushRadius}
                onChange={e =>
                  this.setState({ brushRadius: parseInt(e.target.value, 10) })
                }
              />
            </div>
            <div>
              <label>Lazy-Radius:</label>
              <input
                type="number"
                value={this.state.lazyRadius}
                onChange={e =>
                  this.setState({ lazyRadius: parseInt(e.target.value, 10) })
                }
              />
            </div>
          </div>
          <CanvasDraw

            ref={canvasDraw => (this.saveableCanvas = canvasDraw)}
            brushColor={this.state.color}
            brushRadius={this.state.brushRadius}
            imgSrc= {this.props.imgSrc}
            lazyRadius={this.state.lazyRadius}
            canvasWidth={600}
            canvasHeight={600}
          />
      </DrawingWrapper>
        );
  }
}

CanvasDraw2.propTypes = {
    imgSrc: React.propTypes
}

export default CanvasDraw2
