import React, { Component } from 'react';
import styled from 'styled-components';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import './App.css';

import Main from "./components/Main.jsx"

const Scoreboard = styled.div`

  padding: 40px;
  position: fixed;
  left: 0;
  bottom: 0;
  background-color: lightblue;
  display: flex;

`
const Badge = styled.div`

padding: 20px;
background-color: ${props=>props.color || "red"}

`

const RenderBadges = (props) => {

  const gameStatus = props.gameStatus

  const badgeArray = props.badges

  console.log(badgeArray)

  const mapBadges = badgeArray.map((x) => <Badge color="hi">{x}</Badge>)

  return mapBadges

}


class App extends Component {

  state = {
    score: 0,
    currentGame: "none",
    badges: [0, 0, 2, 0],
    gameStart: true,
    gameOver: false,
  }


  componentDidMount = () => {

  }

  render() {

    const gameStatus = this.state.gameStart;

    return (
      <Router>
        <div>
          {/* <div className="nav">       
      </div> */}

          <Scoreboard>

            <RenderBadges gameStatus={gameStatus} badges={this.state.badges} />
          </Scoreboard>

          <Switch>
            <Route exact path="/">
              <Main />
            </Route>
            <Route path="/map">
            </Route>
            <Route path="/*">
            </Route>
          </Switch>
        </div>

      </Router>
    )
  }
}

export default App;
