                    {/* <input type="submit" value="Submit" onClick={this.submitData}  /> */}
                </form>
                <div className="answers">
                    {this.state.answers.map((x, i) => (
                        <div>{x}</div>
                    ))}
                </div>
                </FormAnswers>

            </CompareFrame>

        )
      }

}

