import React, { Component } from 'react';
// import PropTypes from 'prop-types';
// import tourStops from './data/tourStops';

class CompareImgInput extends React.Component{


    handleSubmit(event) {
        console.log(this.propTypes);
        alert(this.propTypes);
        event.preventDefault();

        const success = "you got it"
        const error = "wrong"
        let submission = "";
        let answerCount = 0; 

        submission = this.state.value;
        let answersArray = this.state.currentData;

        if (!answersArray.includes(submission)){
            return error;
        } else if (answersArray.includes(submission) && answerCount < 3){
            answerCount++;
        } else if (answersArray.includes(submission) && answerCount === 3){
            return success;
        }

    }

    render(){ 
        return(
          <form onSubmit={this.handleSubmit}>
            {/* {this.props.data}   */}
            <input type="text" value={this.state.value} />
             <input type="submit" value="Submit"  />
             {/* <span>{errorMessage}</span> */}
          </form>
        )
      }

}

CompareImgInput.propTypes = {
    data: React.propTypes
}

export default CompareImgInput
